var Stickable = require('moo-jsutil-stickable');
var NavToggle = require('./lib/nav-toggle');
var NavItems = require('./lib/nav-items');
var NavDropdown = require('./lib/nav-dropdown');

var Header = (function () {

  var header = {};

  header.init = function () {
    NavToggle.init();
    NavItems.init();
    NavDropdown.init();
    if($('.PageNav').hasClass('is-stickable')){
      Stickable.stick('.PageNav.is-stickable');
    }
    if($('.Header-toggle').length){
      Stickable.stick('.Header-toggle', -65);
    }
  };

  return header;

})();

module.exports = Header;
