var $ = require('jquery');
var Openable = require('moo-jsutil-openable');

var NavToggle = (function () {

  var navToggle = {};

  navToggle.init = function () {
    $(document).on('click', '[data-nav-toggle]', function(e){
      e.preventDefault();
      var nav = $(this).attr('href');
      Openable.toggle('body');
      Openable.toggle(nav);
    });
  };

  return navToggle;

})();

module.exports = NavToggle;
